import { SET_SCREAMS, LIKE_HUM, UNLIKE_HUM, LOADING_DATA } from "../types";

const initialState = {
  screams: [],
  scream: {},
  loading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case LOADING_DATA:
      return {
        ...state,
        loading: true,
      };
    case SET_SCREAMS:
      return {
        ...state,
        screams: action.payload,
        loading: false,
      };
    case LIKE_HUM:
    case UNLIKE_HUM:
      let index = state.screams.findIndex(
        (screa) => scream.humId === action.payload.humId
      );
      state.screams[index] = action.payload;
      return {
        ...state,
      };
    default:
      return state;
  }
}
