import React, { Component, Fragment } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import MyButton from "../util/MyButton";
import dayjs from "dayjs";
import { Link } from "react-router-dom";

import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import CloseIcon from "@material-ui/icons/Close";

import { connect } from "react-redux";
import { getHum } from "../redux/actions/dataActions";

const styles = {};

class HumDialog extends Component {
  state = {
    open: false,
  };
  handleOpen = () => {
    this.setState({ open: true });
    this.props.getHum(this.props.humId);
  };
  handleClose = () => {
    this.setState({ open: false });
  };
  render() {
    const {
      classes,
      hum: {
        humId,
        body,
        createdAt,
        likeCount,
        commentCount,
        userImage,
        userHandle,
      },
      UI: { loading },
    } = this.props;
  }
}

HumDialog.propTypes = {
  getHum: PropTypes.func.isRequired,
  humId: PropTypes.string.isRequired,
  userHandle: PropTypes.string.isRequired,
  hum: PropTypes.object.isRequired,
  UI: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  hum: state.data.hum,
  UI: state.UI,
});

const mapActionsToProps = {
  getHum,
};

export default connect(
  mapStateToProps,
  mapActionsToProps
)(withStyles(styles)(HumDialog));
