import { SET_SCREAMS, LIKE_HUM, UNLIKE_HUM, LOADING_DATA } from "../types";

const initialState = {
  hums: [],
  hum: {},
  loading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case LOADING_DATA:
      return {
        ...state,
        loading: true,
      };
    case SET_SCREAMS:
      return {
        ...state,
        hums: action.payload,
        loading: false,
      };
    case LIKE_HUM:
    case UNLIKE_HUM:
      let index = state.hums.findIndex(
        (hum) => hum.humId === action.payload.humId
      );
      state.hums[index] = action.payload;
      return {
        ...state,
      };
    default:
      return state;
  }
}
