import React, { Component } from "react";
import withStyles from "@material-ui/core/styles/withStyles";
import { Link } from "react-router-dom";
import dayjs from "dayjs";
import relativeTime from "dayjs/plugin/relativeTime";
import PropTypes from "prop-types";
import DeleteHum from "./DeleteHum";
import HumDialog from "./HumDialog";

//MUI Stuff
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import ChatIcon from "@material-ui/icons/Chat";

import { connect } from "react-redux";
import MyButton from "../util/MyButton";

const styles = {
  card: {
    position: "relative",
    display: "flex",
    marginBottom: 20,
  },
  image: {
    minWidth: 200,
  },
  content: {
    padding: 25,
    objectFit: "cover",
  },
};

class Hum extends Component {
  likedHum = () => {
    if (
      this.props.user.likes &&
      this.props.user.likes.find((like) => like.humId === this.props.hum.humId)
    )
      return true;
    else return false;
  };
  likeHum = () => {
    this.props.likeHum(this.props.hum.humId);
  };
  unlikeHum = () => {
    this.props.unlikeHum(this.props.hum.humId);
  };
  render() {
    dayjs.extend(relativeTime);
    const {
      classes,
      hum: {
        body,
        createdAt,
        userImage,
        userHandle,
        humId,
        likeCount,
        commentCount,
      },
      user: {
        authenticated,
        credentials: { handle },
      },
    } = this.props;

    const deleteButton =
      authenticated && userHandle === handle ? (
        <DeleteHum humId={humId} />
      ) : null;
    return (
      <Card className={classes.card}>
        <CardMedia
          image={userImage}
          title="Profile image"
          className={classes.image}
        />
        <CardContent className={classes.content}>
          <Typography
            variant="h5"
            component={Link}
            to={`/users/${userHandle}`}
            color="primary"
          >
            {userHandle}
          </Typography>
          {deleteButton}
          <Typography variant="body2" color="textSecondary">
            {dayjs(createdAt).fromNow()}
          </Typography>
          <Typography variant="body1">{body}</Typography>
          {likeButton}
          <span>{likeCount} Likes</span>
          <MyButton tip="comments">
            <ChatIcon color="primary" />
          </MyButton>
          <span>{commentCount} comments</span>
          <HumDialog humId={humId} userHandle={userHandle} />
        </CardContent>
      </Card>
    );
  }
}

Hum.propTypes = {
  user: PropTypes.object.isRequired,
  hum: PropTypes.object.isRequired,
  classes: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(withStyles(styles)(Hum));
