export default {
  palette: {
    primary: {
      light: "#b9e6ff",
      main: "#87b4dc",
      dark: "#5684aa",
      contrastText: "#ffffff",
    },
    secondary: {
      light: "#ffffff",
      main: "#f7f6f2",
      dark: "#c4c3bf",
      contrastText: "#011a2d",
    },
  },
  typography: {
    useNextVariants: true,
  },
  form: {
    textAlign: "center",
  },
  image: {
    margin: "20px auto 20px auto",
  },
  pageTitle: {
    margin: "10px auto 10px auto",
  },
  textField: {
    margin: "10px auto 10px auto",
  },
  button: {
    marginTop: 20,
    position: "relative",
