import { SET_SCREAMS, LOADING_DATA, LIKE_HUM, UNLIKE_HUM } from "../types";
import axios from "axios";

export const getHums = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/Hums")
    .then((res) => {
      dispatch({
        type: SET_SCREAMS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_SCREAMS,
        payload: [],
      });
    });
};

export const likeHum = (humId) => (dispatch) => {
  console.log("like");
  axios
    .get(`/Hums/${humId}/like`)
    .then((res) => {
      dispatch({
        type: LIKE_HUM,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};

export const unlikeHum = (humId) => (dispatch) => {
  axios
    .get(`/Hums/${humId}/unlike`)
    .then((res) => {
      dispatch({
        type: UNLIKE_HUM,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
